import React from 'react';


class Carousel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activeIndex: null,
            totalLen: 3, // needs to be update!
            companyList_RC: [
                {
                 image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
                 url: 'https://www.wanted.co.kr/wd/22942',
                 title: '원티드',
                 summary: '프론트엔드 채용 중',
                 styleName: 'active'
                },
                // {
                //  image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
                //  url: 'https://www.wanted.co.kr/wd/22942',
                //  title: 'dffd티드',
                //  summary: '프론트엔드 채용 중',
                //  styleName: 'active'
                // },
                // {
                //  image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
                //  url: 'https://www.wanted.co.kr/wd/22942',
                //  title: '원티',
                //  summary: '프론트엔드 채용 중',
                //  styleName: 'active'
                // }
            ]
        };
    }
    prevSlide = (e) => {
      e.preventDefault();
      //get clicked IDX

      //check if idex == 0 if not
      if(this.state.currIdx != 0){
        this.state.currIdx -=1;

      }
      // -1 active
      //update
      console.log('prev slide');

    }
    nextSlide = (e) => {
      e.preventDefault();
      console.log('next slide');
    }

    render(){
        return  (
          <div id="carouselExample2" className="carousel slide" data-ride="carousel">
            <div className="carousel-inner">
              {this.state.companyList_RC.map((comp, i) => {
                  return (<div className={ "carousel-item " + ( comp.styleName || "") } key={i}>
                    <a
                      href={comp.url}>
                      <img
                          src={comp.image}
                          className="d-block w-100"
                          alt="..." />
                      <h5>{comp.title}</h5>
                    </a>
                  </div>);}
                )
              }
            </div>
            <div className="carousel-control-prev" onClick={(e) => {this.prevSlide(e, )}} role="button" data-slide="prev">
              <span className="carousel-control-prev-icon" aria-hidden="true"><i data-feather="chevron-left"></i></span>
              <span className="sr-only">Previous</span>
            </div>
            <div className="carousel-control-next" onClick={this.nextSlide} role="button" data-slide="next">
              <span className="carousel-control-next-icon" aria-hidden="true"><i data-feather="chevron-right"></i></span>
              <span className="sr-only">Next</span>
            </div>
          </div>
        );
    }
}

export default Carousel;
