import React from 'react';
// import axios from "axios";
import CarouselItem from './CarouselItem';
import CarouselMobileItem from './CarouselMobileItem';
import Slider from "react-slick";
// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const MockUpDataEndPoint = "https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/data.json";
// const cors = require('cors');
class Carousel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activeIndex: 0,
            totalLen: 3, // needs to be update!
            direction: null,
            width: window.innerWidth,
            companyList_RC: [
                {
                 image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
                 url: 'https://www.wanted.co.kr/wd/22942',
                 title: '원티드',
                 summary: '프론트엔드 채용 중',
                 styleName: 'active',
                 order :-1
                },
                {
                 image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/002.jpg',
                 url: 'https://www.wanted.co.kr/wd/22942',
                 title: 'dffd티드',
                 summary: '프론트엔드 채용 중',
                 styleName: 'active',
                 order :0
                },
                {
                 image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/003.jpg',
                 url: 'https://www.wanted.co.kr/wd/22942',
                 title: '원티',
                 summary: '프론트엔드 채용 중',
                 styleName: 'active',
                 order :1
                }
            ]
        };
    }

    prevSlide = (e) => {
      e.preventDefault();
      if(this.state.activeIndex !== 0){
        this.setState({ activeIndex: this.state.activeIndex -1 });
      }else{
        this.setState({ activeIndex: this.state.totalLen-1 });
      }
      this.setState({ direction: "prev" });
    }

    nextSlide = (e) => {
      e.preventDefault();
      if(this.state.activeIndex !== this.state.totalLen-1){
        this.setState({ activeIndex: this.state.activeIndex +1 });
      }else{
        this.setState({ activeIndex: 0 });
      }
      this.setState({ direction: "next" });

    }
    componentWillMount() {
      window.addEventListener('resize', this.handleWindowSizeChange);
      fetch(MockUpDataEndPoint, {mode: 'opaque'}).
       then(response => response.json()).then((repos) => {
           console.log(repos);
           console.log(repos.length);
           this.setState({
             repos: repos
           });
         });

    }

    // make sure to remove the listener
    // when the component is not mounted anymore
    componentWillUnmount() {
      window.removeEventListener('resize', this.handleWindowSizeChange);
    }

    handleWindowSizeChange = () => {
      this.setState({ width: window.innerWidth });
    }

    render(){
        const width = this.state.width;
        const isMobile = width <= 768;
        var settings = {
          dots: true,
          infinite: true,
          speed: 500,
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: true,
          centerMode: true,

        };

        if(isMobile){
          return  (
            <Slider {...settings} >
              {this.state.companyList_RC.map((comp, i) => {
                  return (<CarouselMobileItem
                    styleName={ comp.styleName }
                    url = { comp.url }
                    image = { comp.image }
                    title = { comp.title }
                    summary = { comp.summary }
                    key = { i }
                    isActive = { this.state.activeIndex === i}
                    direction = { this.state.direction }
                    />
                  );
                })
               }
            </Slider>
          ); //return
        }else{
          return  (
            <div className="carousel slide" data-ride="carousel">
              <div className={"carousel-inner "+ this.state.direction + "_" + this.state.activeIndex}>
                {this.state.companyList_RC.map((comp, i) => {
                    return (<CarouselItem
                      styleName={ comp.styleName }
                      url = { comp.url }
                      image = { comp.image }
                      title = { comp.title }
                      summary = { comp.summary }
                      key = { i }
                      isActive = { this.state.activeIndex === i}
                      direction = { this.state.direction }
                      />
                    );
                  })
                 }
              </div>
              <div className="carousel-control-prev" onClick={this.prevSlide} role="button" data-slide="prev">
                <span className="carousel-control-prev-icon" aria-hidden="true"><i data-feather="chevron-left"></i></span>
                <span className="sr-only">Previous</span>
              </div>
              <div className="carousel-control-next" onClick={this.nextSlide} role="button" data-slide="next">
                <span className="carousel-control-next-icon" aria-hidden="true"><i data-feather="chevron-right"></i></span>
                <span className="sr-only">Next</span>
              </div>
            </div>
          ); //return
        } //else

    }//render
}

export default Carousel;
