import React from 'react';
import axios from "axios";
import CarouselItem from './CarouselItem';

const MockUpDataEndPoint = "http://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/data.json";

class Carousel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activeIndex: 0,
            totalLen: 3, // needs to be update!
            direction: null,
            companyList_RC: []
        };
    }
    async componentDidMount() {
      var config = {
          headers: {'Access-Control-Allow-Origin': '*'}
      };
      let { data: companyList_RC } = await axios.get(MockUpDataEndPoint, config);
      this.setState({ companyList_RC });
    }

    prevSlide = (e) => {
      e.preventDefault();
      if(this.state.activeIndex !== 0){
        this.setState({ activeIndex: this.state.activeIndex -1 });
      }else{
        this.setState({ activeIndex: this.state.totalLen-1 });
      }
      this.setState({ direction: "prev" });
    }

    nextSlide = (e) => {
      e.preventDefault();
      if(this.state.activeIndex !== this.state.totalLen-1){
        this.setState({ activeIndex: this.state.activeIndex +1 });
      }else{
        this.setState({ activeIndex: 0 });
      }
      this.setState({ direction: "next" });

    }

    render(){

        return  (
          <div className="carousel slide" data-ride="carousel">
            <div className={"carousel-inner "+ this.state.direction + "_" + this.state.activeIndex}>
              {this.state.companyList_RC.map((comp, i) => {
                  return (<CarouselItem
                    styleName={ comp.styleName }
                    url = { comp.url }
                    image = { comp.image }
                    title = { comp.title }
                    summary = { comp.summary }
                    key = { i }
                    isActive = { this.state.activeIndex === i}
                    direction = { this.state.direction }
                    />
                  );
                })
               }
            </div>
            <div className="carousel-control-prev" onClick={this.prevSlide} role="button" data-slide="prev">
              <span className="carousel-control-prev-icon" aria-hidden="true"><i data-feather="chevron-left"></i></span>
              <span className="sr-only">Previous</span>
            </div>
            <div className="carousel-control-next" onClick={this.nextSlide} role="button" data-slide="next">
              <span className="carousel-control-next-icon" aria-hidden="true"><i data-feather="chevron-right"></i></span>
              <span className="sr-only">Next</span>
            </div>
          </div>
        );
    }
}

export default Carousel;
