import React from 'react';
//Desktop
import CarouselItem from './CarouselItem';
//Mobile
import CarouselMobileItem from './CarouselMobileItem';
import Slider from 'react-slick';
// Assets
import MockUpData from '../assets/mockupdata.json';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
//import axios from "axios";
//const MockUpDataEndPoint = "https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/data.json";

class Carousel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activeIndex: 0,
            totalLen: 3, // needs to be update!
            direction: 'default',
            width: window.innerWidth,
            companyList_RC: MockUpData
        };
    }

    prevSlide = (e) => {

      e.preventDefault();
      if(this.state.activeIndex !== 0){
        this.setState({ activeIndex: this.state.activeIndex -1 });
      }else{
        this.setState({ activeIndex: this.state.totalLen-1 });

        setTimeout(() => {
         this.setState((prevState) => {
           let slides = [...prevState.companyList_RC];

           // pop off last slide to attach another one
             let lastSlide = slides.pop();
           // set its order to -1
             lastSlide = { ...lastSlide, order: -1 };
           // sync order of slides
           slides = slides.map(s => {
             return { ...s, order: s.order + 1 };
           });
           // add popped slide back on
             slides.unshift(lastSlide);

            return { slides };
          });
        }, 500);

      }
      this.setState({ direction: "prev" });
    }
    handlePrev = () => {
      // always have a previous slide beforehand
      this.setState({ activeIndex: this.state.activeIndex -1, direction: "prev" }, () => {
         setTimeout(() => {
          this.setState((prevState) => {
            let slides = [...prevState.slides];
             console.log(slides);
            // pop off last slide to attach another one
              let lastSlide = slides.pop();
              console.log(lastSlide);
            // set its order to -1
              lastSlide = { ...lastSlide, order: -1 };
            // sync order of slides
            slides = slides.map(s => {
              return { ...s, order: s.order + 1 };
            });
            // add popped slide back on
              slides.unshift(lastSlide);
              console.log(lastSlide);
             return { slides, sliding: false };
           });
         }, 500);
       });
    };

    nextSlide = (e) => {
      e.preventDefault();
      if(this.state.activeIndex !== this.state.totalLen-1){
        this.setState({ activeIndex: this.state.activeIndex +1 });
      }else{
        this.setState({ activeIndex: 0 });

        setTimeout(() => {
          this.setState((prevState) => {
            let oldSlides = [...prevState.companyList_RC];
            let hiddenSlide = oldSlides.shift();
            let newHiddenSlide = {
              ...hiddenSlide,
              order: oldSlides[oldSlides.length - 1].order + 1
            };
            oldSlides.push(newHiddenSlide);
            const slides = oldSlides.map(s => {
              return { ...s, order: s.order - 1 };
            });
            return { slides };
          });
        }, 500);

      }
      this.setState({ direction: "next" });

    }
    componentWillMount() {
      window.addEventListener('resize', this.handleWindowSizeChange);

    }
    // componentDidMount(){
    //   return fetch(MockUpDataEndPoint,
    //     { mode: 'no-cors'})
    //   .then(response => {
    //     console.log(response);
    //     return response.text()
    //     })
    //     .then(function(response) {
    //         console.info('fetch()');
    //         //CORS Error
    //         console.log(MockUpData);
    //         this.setState({ companyList_RC : MockUpData});
    //         return response;
    //     })
    //     .catch((error) =>{
    //        // CORS
    //
    //     });
    // }


    // make sure to remove the listener
    // when the component is not mounted anymore
    componentWillUnmount() {
      window.removeEventListener('resize', this.handleWindowSizeChange);
    }

    handleWindowSizeChange = () => {
      this.setState({ width: window.innerWidth });
    }

    mouseOut() {
      console.log("Mouse out!!!");
      this.setState({direction: null});
    }

    mouseOver() {
    console.log("Mouse over!!!");
      this.setState({direction: 'turnoff'});
    }

    render(){
        const { activeIndex, totalLen, direction, width,  companyList_RC} = this.state;
        const isMobile = width <= 768;
        var settings = {
          dots: true,
          infinite: true,
          speed: 500,
          slidesToShow: 1,
          slidesToScroll: 1,
          arrows: true,
          centerMode: true,

        };

        if(isMobile){
          return  (
            <Slider {...settings} >
              {this.state.companyList_RC.map((comp, i) => {
                  return (<CarouselMobileItem
                    styleName={ comp.styleName }
                    url = { comp.info.url }
                    image = { comp.info.image }
                    title = { comp.title }
                    summary = { comp.summary }
                    key = { comp.order }
                    isActive = { this.state.activeIndex === i}
                    direction = { this.state.direction }
                    />
                  );
                })
               }
            </Slider>
          ); //return
        }else{
          return  (
            <div className="carousel slide" data-ride="carousel">
              <div className={"carousel-inner "+ this.state.direction + "_" + this.state.activeIndex} onMouseOut={() => this.mouseOut()} onMouseOver={() => this.mouseOver()}>
                {this.state.companyList_RC.map((comp, i) => {
                    return (<CarouselItem
                      styleName={ comp.styleName }
                      url = { comp.info.url }
                      image = { comp.info.image }
                      title = { comp.title }
                      summary = { comp.summary }
                      key = { comp.order }
                      isActive = { this.state.activeIndex === i}
                      direction = { this.state.direction }
                      />
                    );
                  })
                 }
              </div>
              <div className="carousel-control">
                <div className="carousel-control-prev" role="button" data-slide="prev">
                  <span className="carousel-control-prev-icon" onClick={this.handlePrev} aria-hidden="true"></span>
                </div>
                <div className="carousel-control-next" role="button" data-slide="next">
                  <span className="carousel-control-next-icon" onClick={this.nextSlide} aria-hidden="true"></span>
                </div>
              </div>
            </div>
          ); //return
        } //else

    }//render
}

export default Carousel;
