import React from 'react';
import CarouselItem from './CarouselItem';

class Test extends React.Component {
  state = {
    // slides: [
    //   { order: -1, text: "6" },
    //   { order: 0, text: "1" },
    //   { order: 1, text: "2" },
    //   { order: 2, text: "3" },
    //   { order: 3, text: "4" },
    //   { order: 4, text: "5" }
    // ],
    slides: [
        {
         image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
         url: 'https://www.wanted.co.kr/wd/22942',
         title: '원티드',
         summary: '프론트엔드 채용 중',
         styleName: 'active',
         order :-1
        },
        {
         image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/002.jpg',
         url: 'https://www.wanted.co.kr/wd/22942',
         title: 'dffd티드',
         summary: '프론트엔드 채용 중',
         styleName: 'active',
         order :0
        },
        {
         image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/003.jpg',
         url: 'https://www.wanted.co.kr/wd/22942',
         title: '원티',
         summary: '프론트엔드 채용 중',
         styleName: 'active',
         order :1
        }
    ],
    slideCount: 3,
    sliding: false,
    direction: 0
  };

  handleNext = () => {
    this.setState({ sliding: true, direction: 1 }, () => {
      setTimeout(() => {
        this.setState((prevState) => {
          let oldSlides = [...prevState.slides];
          let hiddenSlide = oldSlides.shift();
          let newHiddenSlide = {
            ...hiddenSlide,
            order: oldSlides[oldSlides.length - 1].order + 1
          };
          oldSlides.push(newHiddenSlide);
          const slides = oldSlides.map(s => {
            return { ...s, order: s.order - 1 };
          });
          return { slides, sliding: false };
        });
      }, 500);
    });
  };

  handlePrev = () => {
    // always have a previous slide beforehand
    this.setState({ sliding: true, direction: -1 }, () => {
       setTimeout(() => {
        this.setState((prevState) => {
          let slides = [...prevState.slides];

          // pop off last slide to attach another one
            let lastSlide = slides.pop();
          // set its order to -1
            lastSlide = { ...lastSlide, order: -1 };
          // sync order of slides
          slides = slides.map(s => {
            return { ...s, order: s.order + 1 };
          });
          // add popped slide back on
            slides.unshift(lastSlide);

           return { slides, sliding: false };
         });
       }, 500);
     });
  };

  render() {
    const { slides, slideCount, sliding, direction } = this.state;
    const slideActionStyle = sliding
    ? direction > 0
      && {
        transform: "translateX(-400px)",
        transition: "transform 500ms ease-in"
      }
      ||
      {
        transform: "translateX(0px)",
        transition: "transform 500ms ease-in"
      }
    : {};

    return (
      // <div>
      //   <div className="left-btn" onClick={!sliding && this.handlePrev}>
      //   <span className="carousel-control-prev-icon" aria-hidden="true"><i data-feather="chevron-left"></i></span>
      //   <span className="sr-only">Previous</span>
      //
      //   </div>
      //   <div
      //     className="item-carousel"
      //     style={slideActionStyle}
      //   >
      //     {slides.map(({order, text}, idx) => {
      //       return <div key={order} className="item">{text}</div>;
      //     })}
      //   </div>
      //   <div className="right-btn" onClick={!sliding && this.handleNext}>
      //   <span className="carousel-control-next-icon" aria-hidden="true"><i data-feather="chevron-right"></i></span>
      //   <span className="sr-only">Next</span>
      //   </div>
      // </div>
      <div className="carousel slide" data-ride="carousel">
        <div className={"carousel-inner "+ this.state.direction} style={slideActionStyle}>
          {this.state.slides.map((comp, i) => {
              return (<CarouselItem
                styleName={ comp.styleName }
                url = { comp.url }
                image = { comp.image }
                title = { comp.title }
                summary = { comp.summary }
                key = { comp.order }
                isActive = { this.state.activeIndex === i}
                direction = { this.state.direction }
                />
              );
            })
           }
        </div>
        <div className="carousel-control-prev" onClick={!sliding && this.handlePrev} role="button" data-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"><i data-feather="chevron-left"></i></span>
          <span className="sr-only">Previous</span>
        </div>
        <div className="carousel-control-next" onClick={!sliding && this.handleNext} role="button" data-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"><i data-feather="chevron-right"></i></span>
          <span className="sr-only">Next</span>
        </div>
      </div>
    );
  }
};
export default Test;
