import React from 'react';


class CarouselItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            companyList_RC: [
                {
                 image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
                 url: 'https://www.wanted.co.kr/wd/22942',
                 title: '원티드',
                 summary: '프론트엔드 채용 중',
                 styleName: 'active'
                },
                {
                 image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
                 url: 'https://www.wanted.co.kr/wd/22942',
                 title: 'dffd티드',
                 summary: '프론트엔드 채용 중',
                 styleName: 'active'
                },
                {
                 image: 'https://s3.ap-northeast-2.amazonaws.com/static.wanted.co.kr/homework/001.jpg',
                 url: 'https://www.wanted.co.kr/wd/22942',
                 title: '원티',
                 summary: '프론트엔드 채용 중',
                 styleName: 'active'
                }
            ]
        };
    }

    render(){
        return  (
          <div>
            {this.state.companyList_RC.map((comp, i) => {
                return (<div className={ "carousel-item " + ( this.props.styleName || "") }>
                  <a
                    href={this.props.url}>
                    <img
                        src={this.props.image}
                        className="d-block w-100"
                        alt="..." />
                    <h5>{this.props.title}</h5>
                  </a>
                </div>);}
              )
            }
          </div>
        );
    }
}

export default CarouselItem;
